import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import { Sidebar } from './components/Sidebar';
import { TableHeader } from './components/TableHeader';
import { TableRow } from './components/TableRow';
import { useScoreData } from './hooks/useScoreData';
import { useModal } from './hooks/useModal';
import styles from './styles/styles';
import printStyles from './styles/printStyles';
import { ScoreModal } from './components/ScoreModal';
import { useTableEvents } from './hooks/useTableEvents';
import { DEFAULT_VALUES } from './constants/tableConstants';
import { calculateScores, calculateTotalScore } from './utils/scoreUtils';
import ReactDOMServer from 'react-dom/server';
import { BusinessCardSheet } from './components/BusinessCardSheet';
import { GroupSheet } from './components/GroupSheet';
import { ParSettingModal } from './components/ParSettingModal';
import { PasswordModal } from './components/PasswordModal';

function App() {
  const {
    rows,
    setRows,
    courseColumns,
    setCourseColumns,
    tournamentName,
    setTournamentName,
    totalDays,
    setTotalDays,
    handleAddCourse,
    handleDeleteCourse,
    handleAddDay,
    handleDeleteDay,
    handleSort,
    validatePlayerAdd
  } = useScoreData();

  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [isPrintMode, setIsPrintMode] = useState(false);
  const [parData, setParData] = useState({});
  const [isParModalOpen, setIsParModalOpen] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const {
    modalData,
    setModalData,
    handleOpenScoreModal,
    handleCloseModal,
    handleScoreChange,
    handleDayChange
  } = useModal();

  const {
    handleFileLoad,
    handleSaveFile,
    handleCaptureTable,
    handleCaptureGroupSheet
  } = useTableEvents({
    rows,
    setRows,
    courseColumns,
    setCourseColumns,
    tournamentName,
    setTournamentName,
    totalDays,
    setTotalDays,
    parData,
    setParData
  });

  if (!isAuthenticated) {
    return (
      <PasswordModal
        isOpen={true}
        onSubmit={() => setIsAuthenticated(true)}
      />
    );
  }

  // 행 추가
  const handleAddRow = () => {
    const newCourses = {};
    courseColumns.forEach(col => {
      newCourses[col] = Array(9).fill(0);
    });
    const newRow = {
      group: '',
      name: '',
      region: '',
      startCourse: '',
      courses: newCourses,
      total: 0
    };
    setRows([...rows, newRow]);
  };

  // 행 업데이트
  const handleUpdateField = (index, field, value) => {
    const updated = [...rows];
    const row = { ...updated[index] };
    row[field] = value;

    // 조 번호가 변경된 경우에만 유효성 검사
    if (field === 'group' && !validatePlayerAdd(row)) {
      return;
    }

    updated[index] = row;
    setRows(updated);
  };

  // 행 삭제
  const handleDeleteRow = (idx) => {
    if (window.confirm('선수를 삭제하시겠습니까?')) {
      setRows(prev => prev.filter((_, i) => i !== idx));
    }
  };

  // 점수 모달 제출
  const handleSubmitScores = () => {
    if (!modalData) return;
    const { rowIndex, allScores } = modalData;

    setRows(prev => {
      const updated = [...prev];
      const row = { ...updated[rowIndex] };

      row.courses = calculateScores(allScores);
      row.total = calculateTotalScore(row.courses);

      updated[rowIndex] = row;
      return updated;
    });

    handleCloseModal();
  };

  const handlePrintBusinessCards = () => {
    const markup = ReactDOMServer.renderToStaticMarkup(
      <BusinessCardSheet rows={rows} tournamentName={tournamentName} />
    );
    const newTab = window.open('', '_blank');
    newTab.document.write(`
      <html>
        <head>
          <meta charset="utf-8">
          <title>명함 인쇄</title>
          <style>
            @page {
              size: A4 landscape;
              margin: 0;
            }
            html {
              background: #eee;
            }
            body {
              margin: 0;
              padding: 0;
              background: #eee;
            }
            .page {
              width: 297mm;
              height: 210mm;
              padding: 10mm;
              margin: 0;
              background: white;
              box-sizing: border-box;
              page-break-after: always;
              display: flex;
              justify-content: center;
              align-items: center;
            }
            .page:last-child {
              page-break-after: auto;
            }
            .card-container {
              width: 277mm;
              height: 190mm;
              display: grid;
              grid-template-columns: repeat(3, 90mm);
              grid-template-rows: repeat(3, 60mm);
              gap: 5mm;
            }
            .card {
              border: 1px solid black;
              padding: 3mm;
              display: flex;
              flex-direction: column;
              background: white;
              height: 60mm;
              box-sizing: border-box;
            }
            .card-header {
              margin-bottom: 2mm;
              padding: 1mm 0;
              text-align: center;
              font-size: 32px;
              font-weight: bold;
              border-bottom: 1px solid black;
              letter-spacing: 1px;
            }
            .card-content {
              display: grid;
              grid-template-columns: 25mm 1fr;
              grid-template-rows: 14mm 26mm;
              gap: 0.5mm;
              border: 1px solid black;
              flex: 1;
            }
            .cell {
              display: flex;
              align-items: center;
              justify-content: center;
              padding: 0.5mm;
              border-right: 1px solid black;
              border-bottom: 1px solid black;
              font-size: 26px;
              font-weight: bold;
              letter-spacing: 0.5px;
            }
            .cell:nth-child(2n) {
              border-right: none;
            }
            .cell:nth-child(3), .cell:nth-child(4) {
              border-bottom: none;
            }
            .cell:last-child {
              font-size: 60px;
              font-weight: bold;
              line-height: 0.9;
              padding: 0;
              letter-spacing: 1px;
            }
            @media print {
              html, body {
                background: white;
                height: auto;
              }
              .page {
                margin: 0;
                padding: 10mm;
                height: 210mm;
                page-break-after: always;
              }
              .page:last-child {
                page-break-after: auto;
              }
            }
          </style>
        </head>
        <body>
          ${Array.from({ length: Math.ceil(rows.length / 9) }, (_, pageIndex) => {
            const pageRows = rows.slice(pageIndex * 9, (pageIndex + 1) * 9);
            if (pageRows.length === 0) return '';
            return `
              <div class="page">
                <div class="card-container">
                  ${pageRows.map(row => `
                    <div class="card">
                      <div class="card-header">${tournamentName}</div>
                      <div class="card-content">
                        <div class="cell">${row.group}그룹</div>
                        <div class="cell">${row.region}</div>
                        <div class="cell">${row.startCourse}</div>
                        <div class="cell">${row.name}</div>
                      </div>
                    </div>
                  `).join('')}
                </div>
              </div>
            `;
          }).filter(Boolean).join('')}
        </body>
      </html>
    `);
    newTab.document.close();
    newTab.focus();
  };

  const handleParSubmit = (courseName, pars) => {
    setParData(prev => ({
      ...prev,
      [courseName]: {
        pars: pars.map(par => par === '' ? '' : parseInt(par))
      }
    }));
  };

  return (
    <>
      <style>{printStyles}</style>
      <div style={styles.container}>
        <div style={{ display: 'flex' }}>
          <div className="print-hide" style={styles.printModeToggle}>
            <div
              onClick={() => setIsPrintMode(!isPrintMode)}
              style={styles.toggleButton(isPrintMode)}
            >
              <div style={styles.toggleKnob(isPrintMode)} />
            </div>
            <span style={styles.toggleLabel}>인쇄 모드</span>
          </div>

          <Sidebar
            onAddCourse={handleAddCourse}
            onDeleteCourse={handleDeleteCourse}
            onAddRow={handleAddRow}
            onAddDay={handleAddDay}
            onDeleteDay={handleDeleteDay}
            onFileLoad={handleFileLoad}
            onSaveFile={handleSaveFile}
            onCaptureTable={handleCaptureTable}
            onCaptureGroupSheet={handleCaptureGroupSheet}
            onPrintBusinessCards={handlePrintBusinessCards}
            onOpenParModal={() => setIsParModalOpen(true)}
            isOpen={isSidebarOpen && !isPrintMode}
            onToggle={() => setIsSidebarOpen(!isSidebarOpen)}
          />

          <div 
            id="printArea"
            style={{ 
              marginLeft: (isSidebarOpen && !isPrintMode) ? '180px' : '30px', 
              flex: 1,
              background: '#ffffff',
              padding: '15px',
              position: 'relative',
              transition: 'margin-left 0.3s ease-in-out'
            }}
          >
            <div id="captureArea">
              <div
                style={styles.header}
                contentEditable
                suppressContentEditableWarning={true}
                onBlur={(e) => setTournamentName(e.target.innerText)}
              >
                {tournamentName || "대회이름입력"}
              </div>

              <div style={{ overflow: 'auto' }}>
                <table style={{
                  ...styles.table,
                  tableLayout: 'fixed',
                  width: '100%'
                }}>
                  <colgroup>
                    {!isPrintMode && <col style={{ width: '70px' }} />}
                    <col style={{ width: '40px' }} />
                    <col style={{ width: '60px' }} />
                    <col style={{ width: '60px' }} />
                    <col style={{ width: '60px' }} />
                    {courseColumns.map((_, idx) => (
                      <col key={idx} style={{ width: '60px' }} />
                    ))}
                    <col style={{ width: '60px' }} />
                    <col style={{ width: '60px' }} />
                  </colgroup>
                  <TableHeader
                    courseColumns={courseColumns}
                    onSort={handleSort}
                    isPrintMode={isPrintMode}
                  />
                  <tbody>
                    {rows.map((row, idx) => (
                      <TableRow
                        key={idx}
                        row={row}
                        rowIndex={idx}
                        courseColumns={courseColumns}
                        onOpenScoreModal={() => handleOpenScoreModal(idx, row.courses, totalDays)}
                        onDeleteRow={handleDeleteRow}
                        onUpdateField={handleUpdateField}
                        allRows={rows}
                        isPrintMode={isPrintMode}
                      />
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>

      {modalData && !isPrintMode && (
        <ScoreModal
          modalData={modalData}
          onSubmit={handleSubmitScores}
          onClose={handleCloseModal}
          onScoreChange={handleScoreChange}
          onDayChange={(day) => handleDayChange(day, rows[modalData.rowIndex].courses)}
        />
      )}

      <ParSettingModal
        isOpen={isParModalOpen}
        onClose={() => setIsParModalOpen(false)}
        onSubmit={handleParSubmit}
        courseColumns={courseColumns}
        parData={parData}
      />
    </>
  );
}

export default App;
