import React from 'react';
import PropTypes from 'prop-types';

const cardStyles = {
  container: {
    width: '297mm',  // A4 height
    height: '210mm',  // A4 width
    padding: '5mm',
    backgroundColor: 'white',
    boxSizing: 'border-box',
    transform: 'rotate(90deg)',
    transformOrigin: 'top left',
    position: 'absolute',
    left: '0',
    top: '297mm',
  },
  cardGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(3, 58mm)',  // 5.8cm per card height
    gridTemplateRows: 'repeat(3, 95mm)',     // 9.5cm per card width
    gap: '5mm',
    height: '100%',
  },
  card: {
    border: '10px solid #000',
    padding: '3mm',
    display: 'flex',
    flexDirection: 'column',
    height: '58mm',
    width: '95mm',
    boxSizing: 'border-box',
    backgroundColor: '#fff',
    transform: 'rotate(-90deg)',
  },
  header: {
    marginBottom: '1mm',
    padding: '1mm 0',
    textAlign: 'center',
    fontSize: '16px',
    fontWeight: 'bold',
    borderBottom: '1px solid #000',
  },
  content: {
    display: 'grid',
    gridTemplateColumns: '1fr 1fr',
    gridTemplateRows: '1fr 1fr',
    gap: '0',
    border: '1px solid #000',
    flex: 1,
    position: 'relative',
    transform: 'scale(0.9)',  // 0.9배 축소
    transformOrigin: 'center center',  // 중앙 기준으로 축소
    margin: 'auto',  // 중앙 정렬
  },
  cell: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '1mm',
    fontSize: '14px',
    fontWeight: 'bold',
    position: 'relative',
  },
  lastColumnCell: {
    '&::after': {
      display: 'none',
    }
  },
  lastRowCell: {
    '&::before': {
      display: 'none',
    }
  },
  nameCell: {
    fontSize: '18px',
  }
};

export const BusinessCardSheet = ({ rows, tournamentName }) => {
  return (
    <div style={{ width: '210mm', height: '297mm', position: 'relative', overflow: 'hidden' }}>
      <div style={cardStyles.container}>
        <div style={cardStyles.cardGrid}>
          {rows.map((row, index) => (
            <div key={index} style={cardStyles.card}>
              <div style={cardStyles.header}>
                {tournamentName}
              </div>
              <div style={cardStyles.content}>
                <div style={{
                  ...cardStyles.cell,
                  borderRight: '1px solid #000',
                  borderBottom: '1px solid #000'
                }}>
                  <span>{row.group + '그룹'}</span>
                </div>
                <div style={{
                  ...cardStyles.cell,
                  borderBottom: '1px solid #000'
                }}>
                  {row.region}
                </div>
                <div style={{
                  ...cardStyles.cell,
                  borderRight: '1px solid #000'
                }}>
                  {row.startCourse}
                </div>
                <div style={{
                  ...cardStyles.cell,
                  ...cardStyles.nameCell
                }}>
                  {row.name}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

BusinessCardSheet.propTypes = {
  rows: PropTypes.arrayOf(
    PropTypes.shape({
      group: PropTypes.string,
      name: PropTypes.string,
      region: PropTypes.string,
      startCourse: PropTypes.string,
    })
  ).isRequired,
  tournamentName: PropTypes.string.isRequired,
};
